CREATE TABLE CryptoAppDB.dbo.Coins (
	Id int IDENTITY(1,1) NOT NULL,
	LongName nvarchar(450) COLLATE SQL_Latin1_General_CP1_CI_AS NOT NULL,
	ShortName nvarchar(450) COLLATE SQL_Latin1_General_CP1_CI_AS NOT NULL,
